public class Computing extends Officer {    private String task;    public Computing(String nameSurname, String phoneNumber, String eposta, String department, String shift, String task) {        super(nameSurname, phoneNumber, eposta, department, shift);        this.task = task;    }    public String getTask() {        return task;    }    public void setTask(String task) {        this.task = task;    }    public void networkSetup(){        System.out.println(this.getNameSurname() + " adlı bilgi işlem elemanı ağ kurulumu yaptı.");    }    @Override    public void Work() {        System.out.println(this.getNameSurname() + " adlı bilgi işlem görevlisi çalışıyor.");    }}